      }
    ]
  }
  render () {
    // const sizeList = [
    //   M,
    //   L,
    //   XL,
    //   XXL
    // ]
    const sizeList = this.state.sizes.map((item, i) => (
                  {item.size}
              )
    )
    return (


          {sizeList}


